import React, {useState} from 'react'
import './index.css'

function Bulb (props) {
    function toggleLight() {
        if (document.getElementById("imgClickAndChange").src == "light_off.jpg"){
            document.getElementById("imgClickAndChange").src = "light_on.png";
        } else {
            document.getElementById("imgClickAndChange").src = "light_off.jpg";
        }
    }

    return (
        <p>
        <img alt="" src="light_off.jpg" 
            style="height: 85px; width: 198px" id="imgClickAndChange" onclick="toggleLight)"/>
        </p>
    )
}

export default Bulb
