import React, {useState} from 'react'
import './index.css'

function Jasper (props) {
  const [adjective, setAdjective] = useState("hungry")
  function toggleLight(){
    let newAdjective = "fun"
    if (adjective === "fun") newAdjective = "hungry"
    setAdjective(newAdjective)
  }

  function toggleLight(){
    const isOn = lightbulb.src.includes("on")
    console.log("is this on?", isOn)
    if(isOn){
        lightbulb.src = "./light_off.jpg"
        lightswitch.className = "lightswitch-off"
        lightswitch.innerText = "OFF"
    } else {
        lightbulb.src = "./light_on.png"
        lightswitch.className = "lightswitch-on"
        lightswitch.innerText = "ON"
    }
}

  return (
    <div>
      <img src="./light_off.jpg" className="lightbulb" />
      <p>
        my dog is {adjective} {props.mood}
      </p>
      <p>
        {props.address}
      </p>
      <button onClick={toggleLight}>
        click me
      </button>
    </div>
  )
}

export default Jasper
